/* Bootstrap 5 simplificado para interacciones bÃ¡sicas */
(() => {
    'use strict';
    document.addEventListener('click', (event) => {
        const target = event.target;
        if (target.matches('[data-bs-toggle="collapse"]')) {
            const selector = target.getAttribute('data-bs-target');
            const collapse = document.querySelector(selector);
            if (collapse) {
                collapse.classList.toggle('show');
            }
        }
        if (target.matches('[data-bs-dismiss="alert"]')) {
            const alert = target.closest('.alert');
            if (alert) {
                alert.remove();
            }
        }
    });
})();
